        var canvas_height = WMap.ySize;
        var chunks = Math.round(MapTiles.mapSize / Minimap.map_tiles_per_minimap_tile);
        var map_size_px = chunks * 400;
        size_x = Math.round((canvas_width * this.size_x) / (map_size_px / 10));
