        pos_x = Math.round((this.size_x / MapTiles.mapSize) * (coord.x - sea_coord.x - this.position_tile_offset.x) * 10);
        pos_y = Math.round((this.size_y / MapTiles.mapSize) * (coord.y - sea_coord.y - this.position_tile_offset.y) * 10);
